  __name: 'App',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const activeName = ref('first');
    //todo 从文件读值
    const words1 = ref([]);
    const words2 = ref([]);
    const poetries1 = ref(['静夜思']);
    const poetries2 = ref(['春晓']);
    onMounted(() => {
      words1.value = JSON.parse(localStorage.getItem('first')) || [];
      words2.value = JSON.parse(localStorage.getItem('second')) || [];
