      if (!newWords.trim()) return; // 空值检查

      // 分割并处理新单词
      const newWordsArray = newWords.split('').filter(word => word.trim() !== '');

      // 去重逻辑
      const existingWords = new Set(words1.value.map(item => item.text));

      // 过滤掉已存在的单词
      const uniqueNewWords = newWordsArray.filter(word => !existingWords.has(word));
      if (!uniqueNewWords.length) {
        return;
      }

